mapper.select:
select a.*, b.cap_file_name as photo from cinema_actor_info a left outer join ( select * from cinema_actor_photo group by cap_cai_seq ) b on a.cai_seq = b.cap_cai_seq where cai_name like '%${keyword}%'
and cai_country = #{country}
limit 10 offset #{offset}

select ceil(count(*)/10) from cinema_actor_info 
where cai_name like '%${keyword}%'

    and cai_country = #{country}

SELECT mi_seq, mi_title, mi_country, mi_year, ( SELECT COUNT(*) FROM movie_actor_casting_info WHERE maci_mi_seq = mi_seq ) AS actor_count FROM movie_info WHERE mi_title like '%${keyword}%'
and mi_country = #{country}
ORDER BY mi_opening_dt DESC LIMIT 10 OFFSET #{offset}

SELECT CEIL(COUNT(*)/10) FROM movie_info WHERE mi_title like '%${keyword}%'
and mi_country = #{country}
ORDER BY mi_opening_dt DESC

SELECT  CEIL(COUNT(*)/10) 
FROM movie_actor_casting_info 
WHERE maci_mi_seq = #{seq}

    and mi_country = #{country}

ORDER BY mi_opening_dt DESC 
LIMIT 10 OFFSET #{offset}

mapper.update:
UPDATE movie_actor_casting_info
SET maci_casting_name=#{maci_casting_name},
    maci_role=#{maci_role}
WHERE maci_seq = #{maci_seq}
